nav {
	display: flex;
	align-items: center;
	justify-content: space-between;
	padding: 1.25rem;
	margin: 1rem clamp(0.5rem, 2vw, 2rem) 0px clamp(0.5rem, 2vw, 2rem);
}
.logo {
	position: relative;
	z-index: 100;
	width: 50px;
	height: 50px;
	aspect-ratio: 1;
	background: none;
	border: none;
	border-radius: 50%;
}
.logo-face {
	width: 50px;
	height: 50px;
	border-radius: 50%;
	overflow: hidden;
	background-color: var(--clr-white);
}
.face-1 {
	position: absolute;
	top: 0;
	right: 0;
	z-index: 100;
}
.face-2 {
	position: absolute;
	z-index: 90;
	top: 0;
	right: 0;
	transform: translate(-10%, 10%);
	background-color: var(--clr-beige);
}
.face-3 {
	position: absolute;
	z-index: 80;
	top: 0;
	right: 0;
	transform: translate(-20%, 20%);
	background-color: var(--clr-white);
	opacity: 1;
}
img.logo-face {
	height: auto;
}
.logo img {
	width: 100%;
}

.nav-links {
	display: flex;
}
.nav-link {
	margin-left: clamp(2rem, 5vw, 4.5rem);
}
.nav-link a {
	font-weight: 400;
}
.nav-menu {
	display: none;
}
@media screen and (max-width: 720px) {
	.nav-links {
		display: none;
	}
	.nav-menu {
		display: block;
		width: 50px;
		height: 50px;
		display: grid;
		align-content: center;
	}
	.nav-menu button {
		display: grid;
		justify-content: center;
		border: none;
		background-color: var(--clr-black);
		height: fit-content;
		padding-block: 0.35rem;
		padding-inline: 0.95rem;
	}
	$size: 8px;
	$margin: 6px;
	.bar {
		margin-left: auto;
		margin-right: auto;
		height: $size;
		width: $size;
		border-radius: 50%;
		margin-bottom: $margin;
	}
	.bar:first-child {
		width: calc($size * 1.6);
		height: calc($size * 1.6);
		background-color: var(--clr-mustard);
	}
	.bar:nth-child(2) {
		width: calc($size * 1.3);
		height: calc($size * 1.3);
		background-color: var(--clr-beige);
	}
	.bar:last-child {
		background-color: var(--clr-white);
	}
}

