.flash-container {
	position: absolute;
	top: 100px;
	overflow: hidden;
	width: 100%;
	height: 600px;
}

.flash {
	position: absolute;
	top: -100px;
	left: 0;
	z-index: 100;
	height: 100vh;
	width: 100vw;
	display: flex;
	align-items: center;
	justify-content: center;
	&__portrait {
		position: relative;
		width: 80vw;
		max-width: 450px;
		height: 80vw;
		max-height: 450px;
		clip-path: polygon(50% 0%, 100% 50%, 50% 100%, 0% 50%);
		filter: grayscale(100%) opacity(25%);
		background-image: url("/assets/square-portrait-1.jpg");
		background-size: contain;
	}
}
.flash-animation {
	animation: toggleVisible 500ms ease-in-out infinite;
}

