.content {
	position: relative;
	z-index: 30;
	display: grid;
	grid-template-columns: 1fr;
	grid-template-rows: repeat(7, 50px);
	width: 80vw;
	max-width: $main-content-max-width;
	margin-left: auto;
	margin-right: auto;
	margin-top: clamp(2rem, 10vw, 4rem);
	@include bp-900 {
		position: relative;
		transform: translateY(-24%);
	}
	& .portrait {
		$portrait-width: clamp(200px, 30vw, 450px);
		position: relative;
		width: $portrait-width;
		height: clamp(250px, 32vw, 420px);
		overflow: hidden;
		& img {
			width: 100%;
		}

		&:first-child {
			justify-self: left;
		}
		&:nth-child(2) {
			justify-self: center;
		}
		&:last-child {
			justify-self: end;
		}
	}
}
